create table posts (id bigint not null auto_increment, created_date datetime, modified_date datetime, author varchar(255), content TEXT not null, title varchar(500) not null, primary key (id)) engine=InnoDB;
create table comments (id bigint not null auto_increment, created_date datetime, modified_date datetime, author varchar(10) not null, content TEXT not null, pwd varchar(10) not null, posts_id bigint, primary key (id),foreign key(posts_id) references posts(id) on delete cascade on update cascade) engine=InnoDB;